import React, { Component } from "react";
// import FilteringOption from "./FilteringOption.js";

class Sorting extends Component {
  render() {
    return (
      <label id="house-selector">
        Sort by
        <select name="house-filter" id="house-filter">
          <option value="all">none</option>
          <option value="Gryffindor">Name</option>
          <option value="Ravenclaw">E-mail</option>
        </select>
      </label>
    );
  }
}

export default Sorting;
