import React, { Component } from "react";
import List from "./List.js";
import ListItem from "./ListItem.js";

class App extends Component {
  constructor() {
    super();
    this.state = {
      loading: false,
      users: []
    };
    this.deleteRecord = this.deleteRecord.bind(this);
    this.filterList = this.filterList.bind(this);
  }

  componentDidMount() {
    this.setState({ loading: true });
    const baseURL = "https://form4earth-2b74.restdb.io/rest/form4earth";
    const headers = {
      "Content-Type": "application/json; charset=utf-8",
      "x-apikey": "5de4e9004658275ac9dc2184",
      "cache-control": "no-cache"
    };
    fetch(baseURL, {
      method: "get",
      headers: headers
    })
      .then(response => response.json())
      .then(data => this.setState({ users: data, loading: false }));
    //   .then(users.map(user => this.setState({ user: user, loading: false })));
  }

  deleteRecord(id) {
    fetch("https://form4earth-2b74.restdb.io/rest/form4earth/" + id, {
      method: "delete",
      headers: {
        "Content-Type": "application/json; charset=utf-8",
        "x-apikey": "5de4e9004658275ac9dc2184",
        "cache-control": "no-cache"
      }
    })
      .then(res => res.json())
      .then(
        this.setState(prevState => {
          const updatedUsers = prevState.users.map(user => {
            if (user._id === id) {
              user.isDeleted = true;
            }
            console.log(user.isDeleted);
            return user;
          });
          return {
            users: updatedUsers
          };
        })
      );

    // this.setState(prevState => {
    //   const updatedUsers = prevState.users.map(user => {
    //     if (user._id === id) {
    //       user.isDeleted = true;
    //     }
    //     console.log(user.isDeleted);
    //     return user;
    //   });
    //   return {
    //     users: updatedUsers
    //   };
    // });
  }

  filterList(id) {
    console.log("papa");
  }

  render() {
    const text = this.state.loading
      ? "loading..."
      : this.state.users.map(user => {
          return <ListItem user={user} deleteRecord={this.deleteRecord} />;
        });
    return (
      <main>
        <div>
          <span>Monthly subscriber</span>
          <label class="filtering" for="yes">
            <input type="radio" name="subscriber" value="yes" id="yes" />
            Yes
          </label>
          <label class="filtering" for="no">
            <input type="radio" name="subscriber" value="no" id="no" />
            No
          </label>
          <label class="filtering" for="all">
            <input
              type="radio"
              name="subscriber"
              value="all"
              id="all"
              checked
            />
            All
          </label>{" "}
        </div>
        <List user={text} />{" "}
      </main>
    );
  }
}

export default App;
