import React, { Component } from "react";
import List from "./List.js";

class App extends Component {
  constructor() {
    super();
    this.state = {
      loading: false,
      users: []
    };
  }

  componentDidMount() {
    this.setState({ loading: true });
    const baseURL = "https://form4earth-2b74.restdb.io/rest/form4earth";
    const headers = {
      "Content-Type": "application/json; charset=utf-8",
      "x-apikey": "5de4e9004658275ac9dc2184",
      "cache-control": "no-cache"
    };
    fetch(baseURL, {
      method: "get",
      headers: headers
    })
      .then(response => response.json())
      .then(data => this.setState({ users: data, loading: false }));
    //   .then(users.map(user => this.setState({ user: user, loading: false })));
  }

  render() {
    const text = this.state.loading
      ? "loading..."
      : this.state.users.map(user => {
          return (
            <tr>
              <td data-field="name">{user.Name}</td>
              <td data-field="email">{user.Email}</td>
              <td data-field="subscription">
                {user.Subscriber ? "yes" : "no"}
              </td>
              <button className="delete"></button>
            </tr>
          );
        });
    return <List user={text} />;
  }
}

export default App;
