import React, { Component } from "react";
import List from "./List.js";
import NewsletterList from "./NewsletterList";
import Tabs from "./Tabs";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = { usersOn: true, newsletterOn: false };

    // This binding is necessary to make `this` work in the callback
    this.handleClick = this.handleClick.bind(this);
  }

  handleClick(e, name) {
    // this.setState(state => ({
    //   isOn: !state.isOn
    // }));

    if (name === "users") {
      this.setState(state => ({
        usersOn: !state.usersOn
      }));
    } else {
      newStyle2 = {
        display: "none"
      };
    }
  }
  render() {
    const newStyle = {
      display: "none"
    };
    return (
      <main>
        <div className="tab-btns">
          <button
            onClick={e => {
              this.handleClick(e, "users");
            }}
          >
            users
          </button>
          <button
            onClick={e => {
              this.handleClick(e, "newsletter");
            }}
          >
            newsletter
          </button>
        </div>
        <div className="tab1">
          <h1>List of users </h1>
          <List style={this.props.usersOn ? null : newStyle} />
        </div>
        <div className="tab2">
          <h1>List of subscribers</h1>
          <NewsletterList style={this.props.newsletterOn ? null : newStyle} />
        </div>
      </main>
    );
  }
}

export default App;
