import React, { Component } from "react";
import FilteringOption from "./FilteringOption.js";

class Filtering extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isHidden: false
    };
  }
  render() {
    return (
      <div>
        <span>Monthly subscriber</span>
        <FilteringOption value="yes" filterList={this.props.filterList} />
        <FilteringOption value="no" filterList={this.props.filterList} />
        <FilteringOption value="all" filterList={this.props.filterList} />
        <label class="filtering" for="no">
          <input
            type="radio"
            name="subscriber"
            value="no"
            id="no"
            onChange={this.props.filterList}
          />
          No
        </label>
        <label class="filtering" for="all">
          <input
            type="radio"
            name="subscriber"
            value="all"
            id="all"
            onChange={this.props.filterList}
          />
          All
        </label>
      </div>
    );
  }
}

export default Filtering;
