import React, { Component } from "react";
import List from "./List.js";
import NewsletterList from "./NewsletterList";
import Tabs from "./Tabs";

class App extends Component {
  constructor() {
    super();
    this.state = { usersOn: true, newsletterOn: false };

    // This binding is necessary to make `this` work in the callback
    this.handleClick = this.handleClick.bind(this);
  }

  handleClick(e, name) {
    // this.setState(state => ({
    //   isOn: !state.isOn
    // }));

    if (name === "users") {
      this.setState({
        usersOn: true,
        newsletterOn: false
      });
      console.log(this.state.usersOn);
    } else {
      this.setState({
        usersOn: false,
        newsletterOn: true
      });
    }
  }
  render() {
    const newStyle = {
      // display: "none"
      color: "blue"
    };
    return (
      <main>
        <div className="tab-btns">
          <button
            onClick={e => {
              this.handleClick(e, "users");
            }}
          >
            users
          </button>
          <button
            onClick={e => {
              this.handleClick(e, "newsletter");
            }}
          >
            newsletter
          </button>
        </div>
        <div className="tab1">
          <h1>List of users </h1>
          <List visibility={this.state.usersOn} />
        </div>
        <div className="tab2">
          <h1>List of subscribers</h1>
          <NewsletterList visibility={this.state.newsletterOn} />
        </div>
      </main>
    );
  }
}

export default App;
