import React, { Component } from "react";

class App extends Component {
  constructor() {
    super();
    this.state = {
      loading: false,
      users: []
    };
  }

  componentDidMount() {
    this.setState({ loading: true });
    const baseURL = "https://form4earth-2b74.restdb.io/rest/form4earth/";
    const headers = {
      "Content-Type": "application/json; charset=utf-8",
      "x-apikey": "5de4e9004658275ac9dc2184",
      "cache-control": "no-cache"
    };
    fetch(baseURL, {
      method: "get",
      headers: headers
    })
      .then(response => response.json())
      //   .then(data => this.setState({ user: data, loading: false }));
      .then(data =>
        data.forEach(user => this.setState({ user: user, loading: false }))
      );
    //   .then(users.map(user => this.setState({ user: user, loading: false })));
  }

  render() {
    const text = this.state.loading ? "loading..." : this.state.user[0];
    return (
      <div>
        <p>{text}</p>
      </div>
    );
  }
}

export default App;
