import React, { Component } from "react";
import List from "./List.js";
import NewsletterList from "./NewsletterList";
import Tabs from "./Tabs";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = { isOn: true };

    // This binding is necessary to make `this` work in the callback
    this.handleClick = this.handleClick.bind(this);
  }

  handleClick(e, name) {
    // this.setState(state => ({
    //   isOn: !state.isOn
    // }));
    console.log(name);
    if (name === "users") {
      const newStyle = {
        display: "none"
      };
    } else {
      const newStyle2 = {
        display: "none"
      };
    }
  }
  render() {
    return (
      <main>
        <div className="tab-btns">
          <button
            onClick={e => {
              this.handleClick(e, "users");
            }}
          >
            {this.state.isOn ? "ON" : "OFF"}
            users
          </button>
          <button
            onClick={e => {
              this.handleClick(e, "newsletter");
            }}
          >
            {this.state.isOn ? "ON" : "OFF"}
            newsletter
          </button>
        </div>
        <div className="tab1">
          <h1>List of users </h1>
          <List />
        </div>
        <div className="tab2">
          <h1>List of subscribers</h1>
          <NewsletterList />
        </div>
      </main>
    );
  }
}

export default App;
