import React, { Component } from "react";
import List from "./List.js";
import ListItem from "./ListItem.js";
import Filtering from "./Filtering";

class App extends Component {
  constructor() {
    super();
    this.state = {
      loading: false,
      users: []
    };
    this.deleteRecord = this.deleteRecord.bind(this);
    this.filterList = this.filterList.bind(this);
  }

  componentDidMount() {
    this.setState({ loading: true });
    const baseURL = "https://form4earth-2b74.restdb.io/rest/form4earth";
    const headers = {
      "Content-Type": "application/json; charset=utf-8",
      "x-apikey": "5de4e9004658275ac9dc2184",
      "cache-control": "no-cache"
    };
    fetch(baseURL, {
      method: "get",
      headers: headers
    })
      .then(response => response.json())
      .then(data => this.setState({ users: data, loading: false }));
    //   .then(users.map(user => this.setState({ user: user, loading: false })));
  }

  deleteRecord(id) {
    fetch("https://form4earth-2b74.restdb.io/rest/form4earth/" + id, {
      method: "delete",
      headers: {
        "Content-Type": "application/json; charset=utf-8",
        "x-apikey": "5de4e9004658275ac9dc2184",
        "cache-control": "no-cache"
      }
    })
      .then(res => res.json())
      .then(
        this.setState(prevState => {
          const updatedUsers = prevState.users.map(user => {
            if (user._id === id) {
              user.isDeleted = true;
            }
            console.log(user.isDeleted);
            return user;
          });
          return {
            users: updatedUsers
          };
        })
      );
  }

  filterList(e) {
    e.target.checked = true;

    // check which filtering option is checked
    const value = e.target.id;

    // if all then pass to sorting, else filter by that house
    // if (value === "all") {
    //   filteredList = users.filter(student => {
    //     return true;
    //   });
    // } else {
    //   filteredList = people.filter(filterByHouse);
    // }

    // function filterByHouse(person) {
    //   if (person.house === value) {
    //     return true;
    //   } else {
    //     return false;
    //   }
    // }

    //end

    //state
    this.setState(prevState => {
      const filteredUsers = prevState.users.map(user => {
        if (value === "all") {
          return user;
        } else if (value === "yes") {
          if (user.Subscriber) {
            return user;
          }
        } else {
          if (!user.Subscriber) {
            return user;
          }
        }
      });
      return {
        users: filteredUsers
      };

      //     if (value === "all") {
      //       user.isDeleted = true;
      //     }
      //     console.log(user.isDeleted);
      //     return user;
      //   });
      //   return {
      //     users: filteredUsers
      //   };
    });
  }

  render() {
    const text = this.state.loading
      ? "loading..."
      : this.state.users.map(user => {
          return <ListItem user={user} deleteRecord={this.deleteRecord} />;
        });
    return (
      <main>
        <Filtering filterList={this.filterList} />
        <List user={text} />{" "}
      </main>
    );
  }
}

export default App;
