import React, { Component } from "react";
import List from "./List.js";
import ListItem from "./ListItem.js";

class App extends Component {
  constructor() {
    super();
    this.state = {
      loading: false,
      users: []
    };
    this.deleteRecord = this.deleteRecord.bind(this);
  }

  componentDidMount() {
    this.setState({ loading: true });
    const baseURL = "https://form4earth-2b74.restdb.io/rest/form4earth";
    const headers = {
      "Content-Type": "application/json; charset=utf-8",
      "x-apikey": "5de4e9004658275ac9dc2184",
      "cache-control": "no-cache"
    };
    fetch(baseURL, {
      method: "get",
      headers: headers
    })
      .then(response => response.json())
      .then(data => this.setState({ users: data, loading: false }));
    //   .then(users.map(user => this.setState({ user: user, loading: false })));
  }

  deleteRecord(id) {
    fetch("https://bandsdatabase-76bc.restdb.io/rest/bands/" + id, {
      method: "delete",
      headers: {
        "Content-Type": "application/json; charset=utf-8",
        "x-apikey": "5de4e9004658275ac9dc2184",
        "cache-control": "no-cache"
      }
    })
      .then(res => res.json())
      .then(data => {
        //delete from DOM
        document.querySelector(`.flip-card[data-bandid="${id}"`).remove();
      });

    // this.setState(prevState => {
    //   const updatedUsers = prevState.users.map(user => {
    //     if (user._id === id) {
    //       user.isDeleted = true;
    //     }
    //     console.log(user.isDeleted);
    //     return user;
    //   });
    //   return {
    //     users: updatedUsers
    //   };
    // });
  }

  render() {
    const text = this.state.loading
      ? "loading..."
      : this.state.users.map(user => {
          return <ListItem user={user} deleteRecord={this.deleteRecord} />;
        });
    return <List user={text} />;
  }
}

export default App;
